00733 static int
00734 tcfs_read (const char *fuse_path, char *buf, size_t size, off_t offset,
00735            struct fuse_file_info *fi)
00736 {
00737   (void)size;
00738   (void)fi;
00739 
00740   logMessage ("Calling read\n");
00741   FILE *path_ptr, *tmpf;
00742   char *path;
00743   int res;
00744 
00745   // Retrieve the username
00746   char username_buf[1024];
00747   size_t username_buf_size = 1024;
00748   get_user_name (username_buf, username_buf_size);
00749 
00750   const char *enc_fuse_path = encrypt_path (fuse_path, password);
00751   path = prefix_path (enc_fuse_path, root_path);
00752   logMessage ("\tread on %s\n", path);
00753 
00754   path_ptr = fopen (path, "r");
00755   tmpf = tmpfile ();
00756 
00757   // Get key size
00758   char *size_key_char = malloc (sizeof (char) * 20);
00759   if (tcfs_getxattr (fuse_path, "user.key_len", size_key_char, 20) == -1)
00760     {
00761       perror ("Could not get file key size");
00762       return -errno;
00763     }
00764   ssize_t size_key = strtol (size_key_char, NULL, 10);
00765 
00766   // Retrive the file key
00767   unsigned char *encrypted_key = malloc ((size_key + 1) * sizeof (char));
00768   encrypted_key[size_key] = '\0';
00769   if (tcfs_getxattr (fuse_path, "user.key", (char *)encrypted_key, size_key)
00770       == -1)
00771     {
00772       perror ("Could not get encrypted key for file in tcfs_read");
00773       return -errno;
00774     }
00775 
00776   // Decrypt the file key
00777   unsigned char *decrypted_key;
00778   decrypted_key = decrypt_string (encrypted_key, password);
00779 
00780   /* Decrypt*/
00781   if (do_crypt (path_ptr, tmpf, DECRYPT, decrypted_key) != true)
00782     {
00783       perror ("Err: do_crypt cannot decrypt file");
00784       return -errno;
00785     }
00786 
00787   /* Something went terribly wrong if this is the case. */
00788   if (path_ptr == NULL || tmpf == NULL)
00789     return -errno;
00790 
00791   if (fflush (tmpf) != 0)
00792     {
00793       perror ("Err: Cannot flush file in read process");
00794       return -errno;
00795     }
00796   if (fseek (tmpf, offset, SEEK_SET) != 0)
00797     {
00798       perror ("Err: cannot fseek while reading file");
00799       return -errno;
00800     }
00801 
00802   /* Read our tmpfile into the buffer. */
00803   res = fread (buf, 1, file_size (tmpf), tmpf);
00804   if (res == -1)
00805     {
00806       perror ("Err: cannot fread whine in read");
00807       res = -errno;
00808     }
00809 
00810   fclose (tmpf);
00811   fclose (path_ptr);
00812   free (encrypted_key);
00813   free (decrypted_key);
00814   return res;
00815 }
