00829 static int
00830 tcfs_write (const char *fuse_path, const char *buf, size_t size, off_t offset,
00831             struct fuse_file_info *fi)
00832 {
00833   (void)fi;
00834   logMessage ("Called write\n");
00835 
00836   FILE *path_ptr, *tmpf;
00837   char *path;
00838   int res;
00839   int tmpf_descriptor;
00840 
00841   const char *enc_fuse_path = encrypt_path (fuse_path, password);
00842   path = prefix_path (enc_fuse_path, root_path);
00843   logMessage ("\twrite on %s\n", path);
00844 
00845   path_ptr = fopen (path, "r+");
00846   tmpf = tmpfile ();
00847   tmpf_descriptor = fileno (tmpf);
00848 
00849   // Get the key size
00850   char *size_key_char = malloc (sizeof (char) * 20);
00851   if (tcfs_getxattr (fuse_path, "user.key_len", size_key_char, 20) == -1)
00852     {
00853       perror ("Could not get file key size");
00854       return -errno;
00855     }
00856   ssize_t size_key = strtol (size_key_char, NULL, 10);
00857 
00858   // Retrieve the file key
00859   unsigned char *encrypted_key
00860       = malloc (sizeof (unsigned char) * (size_key + 1));
00861   encrypted_key[size_key] = '\0';
00862   if (tcfs_getxattr (fuse_path, "user.key", (char *)encrypted_key, size_key)
00863       == -1)
00864     {
00865       perror ("Could not get file encrypted key in tcfs write");
00866       return -errno;
00867     }
00868 
00869   // Decrypt the file key
00870   unsigned char *decrypted_key = malloc (sizeof (unsigned char) * 33);
00871   decrypted_key[32] = '\0';
00872   decrypted_key = decrypt_string (encrypted_key, password);
00873 
00874   /* Something went terribly wrong if this is the case. */
00875   if (path_ptr == NULL || tmpf == NULL)
00876     {
00877       perror ("Something went terribly wrong, cannot create new files");
00878       return -errno;
00879     }
00880 
00881   /* if the file to write to exists, read it into the tempfile */
00882   if (tcfs_access (fuse_path, R_OK) == TCFS_SUCCESS && file_size (path_ptr) > 0)
00883     {
00884       if (do_crypt (path_ptr, tmpf, DECRYPT, decrypted_key) == false)
00885         {
00886           perror ("do_crypt: Cannot cypher file\n");
00887           return --errno;
00888         }
00889       rewind (path_ptr);
00890       rewind (tmpf);
00891     }
00892 
00893   /* Read our tmpfile into the buffer. */
00894   res = pwrite (tmpf_descriptor, buf, size, offset);
00895   if (res == -1)
00896     {
00897       logMessage ("%d\n", res);
00898       perror ("pwrite: cannot read tmpfile into the buffer\n");
00899       res = -errno;
00900     }
00901 
00902   /* Encrypt*/
00903   if (do_crypt (tmpf, path_ptr, ENCRYPT, decrypted_key) == false)
00904     {
00905       perror ("do_crypt 2: cannot cypher file\n");
00906       return -errno;
00907     }
00908 
00909   fclose (tmpf);
00910   fclose (path_ptr);
00911   free (encrypted_key);
00912   free (decrypted_key);
00913 
00914   return res;
00915 }
