00980                size_t size, int flags)
00981 {
00982   const char *enc_fuse_path = encrypt_path (fuse_path, password);
00983   const char *path = prefix_path (enc_fuse_path, root_path);
00984   logMessage ("\tsetxattr encrypt_path %s\n", path);
00985 
00986   int res = 1;
00987   if ((res = lsetxattr (path, name, value, size, flags)) == -1)
00988     perror ("tcfs_lsetxattr");
00989   if (res == -1)
00990     return -errno;
00991   return TCFS_SUCCESS;
00992 }
00993 
01030 static int
01031 tcfs_create (const char *fuse_path, mode_t mode, struct fuse_file_info *fi)
01032 {
01033   (void)fi;
01034   (void)mode;
01035   logMessage ("Called create on %s\n", fuse_path);
01036 
01037   const char *enc_fuse_path = encrypt_path_and_filename (fuse_path, password);
01038   const char *fullpath = prefix_path (enc_fuse_path, root_path);
01039   logMessage ("\tcreating %s\n", fullpath);
01040 
01041   FILE *res;
01042   res = fopen (fullpath, "w");
01043   if (res == NULL)
01044     return -errno;
01045 
01046   // Flag file as encrypted
01047   if (tcfs_setxattr (fuse_path, "user.encrypted", "true", 4, 0)
01048       != TCFS_SUCCESS) //(fsetxattr(fileno(res), "user.encrypted", "true", 4, 0) != 0)
01049     {
01050       fclose (res);
01051       return -errno;
01052     }
01053 
01054   // Generate and set a new encrypted key for the file
01055   unsigned char *key = malloc (sizeof (unsigned char) * 33);
01056   key[32] = '\0';
01057   generate_key (key);
01058 
01059   if (key == NULL)
01060     {
01061       perror ("cannot generate file key");
01062       return -errno;
01063     }
01064   if (is_valid_key (key) == false)
01065     {
01066       perror ("Generated key size invalid\n");
01067       return -1;
01068     }
01069 
01070   // Encrypt the generated key
01071   int encrypted_key_len;
01072   unsigned char *encrypted_key
01073       = encrypt_string (key, password, &encrypted_key_len);
01074 
01075   // Set the file key
01076   if (tcfs_setxattr (fuse_path, "user.key", (const char *)encrypted_key,
01077                      encrypted_key_len, 0)
01078       != 0) //(fsetxattr(fileno(res), "user.key", encrypted_key, 32, 0) != 0)
01079     {
01080       perror ("Err setting key xattr");
01081       return -errno;
01082     }
01083   // Set key size
01084   char encrypted_key_len_char[20];
01085   snprintf (encrypted_key_len_char, sizeof (encrypted_key_len_char), "%d",
01086                 encrypted_key_len);
01087   if (tcfs_setxattr (fuse_path, "user.key_len", encrypted_key_len_char,
01088                      sizeof (encrypted_key_len_char), 0)
01089       != TCFS_SUCCESS) //(fsetxattr(fileno(res), "user.key", encrypted_key, 32, 0) != 0)
01090     {
01091       perror ("Err setting key_len xattr");
01092       return -errno;
01093     }
01094 
01095   free (encrypted_key);
01096   free (key);
01097   fclose (res);
01098   return TCFS_SUCCESS;
01099 }
