01110 static int
01111 tcfs_release (const char *fuse_path, struct fuse_file_info *fi)
01112 {
01113   const char *enc_fuse_path = encrypt_path_and_filename (fuse_path, password);
01114   const char *path = prefix_path (enc_fuse_path, root_path);
01115   logMessage ("\trelease %s\n", path);
01116 
01117   /* Close the file */
01118   int res = close (fi->fh);
01119   if (res == -1)
01120     return -errno;
01121 
01122   /* Free the path */
01123   free ((void *)path);
01124   free ((void *)enc_fuse_path);
01125 
01126   return TCFS_SUCCESS;
01127 }
