01139 static int
01140 tcfs_fsync (const char *fuse_path, int isdatasync, struct fuse_file_info *fi)
01141 {
01142   /* Get the real path */
01143   const char *enc_fuse_path = encrypt_path_and_filename (fuse_path, password);
01144   const char *path = prefix_path (enc_fuse_path, root_path);
01145   logMessage ("\tfsync %s\n", path);
01146 
01147   /* Synchronize the file's in-core state with storage device */
01148   int res;
01149   if (isdatasync)
01150     res = fdatasync ((int)fi->fh); // God, please do not let this overflow
01151   else
01152     res = fsync ((int)fi->fh); // Also this
01153 
01154   if (res == -1)
01155     return -errno;
01156 
01157   /* Free the path */
01158   free ((void *)path);
01159 
01160   return TCFS_SUCCESS;
01161 }
