01338                     "-d <dest_path> -p <password> [fuse arguments]";
01339 
01343 static char args_doc[] = "";
01344 
01348 static struct argp_option options[]
01349     = { { "source", 's', "SOURCE", 0, "Source file path", -1 },
01350         { "destination", 'd', "DESTINATION", 0, "Destination file path", -1 },
01351         { "password", 'p', "PASSWORD", 0, "Password", -1 },
01352         { NULL } };
