01357 struct arguments
01358 {
01359   char *source;
01360   char *destination;
01361   char *password;
01362 };
01363 
01373 static error_t
01374 parse_opt (int key, char *arg, struct argp_state *state)
01375 {
01376   struct arguments *arguments = state->input;
01377 
01378   switch (key)
01379     {
01380     case 's':
01381       arguments->source = arg;
01382       break;
01383     case 'd':
01384       arguments->destination = arg;
01385       break;
01386     case 'p':
01387       arguments->password = arg;
01388       break;
01389     case ARGP_KEY_ARG:
01390       return ARGP_ERR_UNKNOWN;
01391     default:
01392       return ARGP_ERR_UNKNOWN;
01393     }
01394 
01395   return TCFS_SUCCESS;
01396 }
01397 
01401 static struct argp argp = { options, parse_opt, args_doc, doc, 0, NULL, NULL };
