01411 int
01412 main (int argc, char *argv[])
01413 {
01414   umask (0);
01415 
01416   struct arguments arguments;
01417 
01418   arguments.source = NULL;
01419   arguments.destination = NULL;
01420   arguments.password = NULL;
01421 
01422   argp_parse (&argp, argc, argv, 0, 0, &arguments);
01423 
01424   if (arguments.source == NULL || arguments.destination == NULL
01425       || arguments.password == NULL)
01426     {
01427       logMessage ("Err: You need to specify at least 3 arguments\n");
01428       return -ERR_inval_arg_len;
01429     }
01430 
01431   logMessage ("Source: %s\n", arguments.source);
01432   logMessage ("Destination: %s\n", arguments.destination);
01433   root_path = arguments.source;
01434 
01435   if (is_valid_key ((unsigned char *)arguments.password) == TCFS_SUCCESS)
01436     {
01437       logMessage ("ERR: Inserted key not valid\n");
01438       return -ERR_inval_key;
01439     }
01440 
01441   struct fuse_args args_fuse = FUSE_ARGS_INIT (0, NULL);
01442   fuse_opt_add_arg (&args_fuse, "./tcfs");
01443   fuse_opt_add_arg (&args_fuse, arguments.destination);
01444   fuse_opt_add_arg (&args_fuse,
01445                     "-f"); // TODO: this is forced for now, but will be passed
01446                            // via options in the future
01447   fuse_opt_add_arg (&args_fuse,
01448                     "-s"); // TODO: this is forced for now, but will be passed
01449                            // via options in the future
01450 
01451   // Print what we are passing to fuse TODO: This will be removed
01452   for (int i = 0; i < args_fuse.argc; i++)
01453     {
01454       logMessage ("%s ", args_fuse.argv[i]);
01455     }
01456   logMessage ("\n");
01457 
01458   password = arguments.password;
01459 
01460   return fuse_main (args_fuse.argc, args_fuse.argv, &tcfs_oper, NULL);
01461 }
