00035       if (choice != 1 && choice != 2 && choice != 3)
00036         printf ("Err: Select 1 or 2\n");
00037     }
00038   while (choice != 1 && choice != 2 && choice != 3);
00039   printf ("You chose %d\n", choice);
00040 
